import "./ProductScreen.css";


export default function ProductScreen() {
  return (
    <div className="productscreen">
     
          <div className="productscreen__left">
            <div className="left__image">
              <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSk_5GHW1Ms3ftphhuoMu_aE8Ge2oYTOIb0Dw&usqp=CAU" alt="produit1"/>
            </div>
            <div className="left__info">
              <p className="left__name"></p>
              <p>Price: $55</p>
              <p> Description:"L'aloé vera est une plante d'intérieur décorative, facile d'entretien et peu exigeante tout au long de l'année. Elle es utilisée pour ses vertus médicinales.</p>
            </div>
          </div>
          <div className="productscreen__right">
            <div className="right__info">
              <p>
                Price:
                <span>$50</span>
              </p>
              <p>
                Status:
                <span>
                  in Stock
                </span>
              </p>
              <p>
                Qty
               
              </p>
              <p>
                <select>
                  <option value="1">1</option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                  <option value="4">4</option>

                </select>
              </p>
              <button type="button"> Add to carte</button>
            </div>
          </div>
    
      </div>
  )
}

