import { Link } from "react-router-dom";
import "./CartItem.css";
export const CartItem = () => {
  return (
    <div className="cartitem">
      <div className="cartitem__image">
        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSk_5GHW1Ms3ftphhuoMu_aE8Ge2oYTOIb0Dw&usqp=CAU" alt="/" />
      </div>
      <Link to={`/product/${55}`} className="cartItem__name">
        <p>product1</p>
      </Link>
      <p className="cartitem__price">$50</p>
      <select className="cartItem__select">
      <option value="1">1</option>
      <option value="2">2</option>
      <option value="3">3</option>
      <option value="4">4</option>
       
      </select>
      <button className="cartitem_deleteBtn"><i className="fas fa-trash"></i> </button>
    </div>
  )
}
export default CartItem;

